import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Register = () => {
  const [error, setError] = useState(false);
  const [inputs, setInputs] = useState({
    username: "",
    mail: "",
    password: "",
  });
  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  // console.log(inputs);
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (inputs.length === 0) {
      setError(true);
    }
    try {
      const res = await axios.post("/auth/registerIn", inputs);
      console.log(res);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="auth">
      <h1>REGISTER - DONATORS</h1>
      <form>
        <label>Username</label>
        <input
          required
          type="text"
          placeholder="USERNAME"
          name="username"
          onChange={handleChange}
        />
        {error}
        <label className="label">mail</label>
        <input required type="email" placeholder="MAIL" name="mail" onChange={handleChange} />
        <input
          required
          type="password"
          placeholder="PASSWORD"
          name="password"
          onChange={handleChange}
        />
        {/* <input required type="password" placeholder="CONFIRM PASSWORD" /> */}
        {/* donator type */}
        {/* <input type="text" placeholder="FIRM NAME (for organisations)" />
        <input type="text" placeholder="NAME (for individuals)" />
        <input required type="text" placeholder="address" />
        <input required type="date" placeholder="email" />
        <input required type="password" placeholder="password" />
        <input required type="text" placeholder="username" />
        <input required type="email" placeholder="email" />
        <input required type="password" placeholder="password" />
        <input required type="text" placeholder="username" />
        <input required type="email" placeholder="email" />
        <input required type="password" placeholder="password" /> */}
        <button onClick={handleSubmit}>Register</button>
        <p>This is an Error!</p>
        <span>
          Already have an Account? <Link to="/login">Login</Link>
        </span>
      </form>
    </div>
  );
};

export default Register;
