import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
const Register = () => {
  const [inputs, setInputs] = useState({
    username: "",
    mail: "",
    password: "",
    confirmPassword: "",
    donatorType: "",
    firmName: "",
    name: "",
    address: "",
    PinCode: "",
    city: "",
    state: "",
    country: "",
    contactNumber: "",
  });
  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  // console.log(inputs);
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/auth/registerIn", inputs);
      console.log(res);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="auth">
      <h1>REGISTER - DONATORS</h1>
      <form>
        <label>Username</label>
        <input
          required
          type="text"
          placeholder="USERNAME"
          name="username"
          onChange={handleChange}
        />
        <label>E-Mail</label>
        <input required type="email" placeholder="E-MAIL" name="mail" onChange={handleChange} />
        <label>Password</label>
        <input
          required
          type="password"
          placeholder="PASSWORD"
          name="password"
          onChange={handleChange}
        />
        <input
          required
          type="password"
          placeholder="CONFIRM PASSWORD"
          name="confirmPassword"
          onChange={handleChange}
        />
        {/* donator type */}
        <label>FIRM NAME (for organisations)</label>
        <input
          type="text"
          placeholder="FIRM NAME (for organisations)"
          name="firmName"
          onChange={handleChange}
        />
        <input type="text" placeholder="NAME (for individuals)" />
        <input required type="text" placeholder="address" />
        <input required type="date" placeholder="email" />
        <input required type="password" placeholder="password" />
        <input required type="text" placeholder="username" />
        <input required type="email" placeholder="email" />
        <input required type="password" placeholder="password" />
        <input required type="text" placeholder="username" />
        <input required type="email" placeholder="email" />
        <input required type="password" placeholder="password" />
        <button onClick={handleSubmit}>Register</button>
        <p>This is an Error!</p>
        <span>
          Already have an Account? <Link to="/login">Login</Link>
        </span>
      </form>
    </div>
  );
};

export default Register;
