import * as yup from "yup";
const passwordRules = /^(?==.*\d)(?=.*[a-z])(?=.*{A-Z}).{5,}$/;
export const basicSchema = yup.object().shape({
  username: yup.string().min(2, "Mininum 2 characters").max(30, "Maximum 30 characters").required("Required"),
  mail: yup.string().email("Please enter valid email ").required("Required"),
  password: yup.string().min(8).minLowercase(1, 'password must contain at least 1 lower case letter').minUppercase(1, 'password must contain at least 1 upper case letter').minNumbers(1, 'password must contain at least 1 number').minSymbols(1, 'password must contain at least 1 special character').matches(passwordRules, {
    message: "Please enter a stronger password"
  }).required("Required"),
  confirmPassword: yup.string().oneOf([yup.ref("password"), null], "Password must match").required("Required")
});
