import React from "react";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
const Register = () => {
  const[inputs, setInputs] = useState({
    username: "",
    mail: "",
    password: "",
    confirmPassword: "",
    donatorType: "",
    firmName: "",
    name: "",
    address: "",
    PinCode: "",
    city: "",
    state: "",
    country: "",
    contactNumber: "",
  });
  const navigate = useNavigate();
  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
  // console.log(inputs);
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/auth/registerIn", inputs);
      navigate("/donator");
      console.log(res);
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="auth">
      <h1>REGISTER - DONATORS</h1>
      <form>
        <label>Username</label>
        <input
          type="text"
          placeholder="ENTER USERNAME HERE"
          name="username"
          onChange={handleChange}
        />
        <label>E-Mail</label>
        <input
          type="email"
          placeholder="ex: myname@gmail.com"
          name="mail"
          onChange={handleChange}
        />
        <label>Password</label>
        <input
          type="password"
          placeholder="ENTER A PASSWORD"
          name="password"
          onChange={handleChange}
        />
        <input
          type="password"
          placeholder="CONFIRM YOUR PASSWORD"
          name="confirmPassword"
          onChange={handleChange}
        />
        {/* donator type */}
        <label>DONATOR TYPE : </label>
        <select onChange={handleChange} name="donatorType" >
          {/* <option value="DEFAULT" disabled> */}
            CHOOSE AN OPTION
          {/* </option> */}
          <option value="Organisation">Organisation</option>
          <option value="Individual">Individual</option>
          <option value="Others">Others</option>
        </select>
        <label>FIRM NAME (for organisations)</label>
        <input
          type="text"
          placeholder="ENTER ORGANISATION NAME"
          name="firmName"
          onChange={handleChange}
        />
        <label>NAME (for individuals)</label>
        <input type="text" placeholder="ENTER YOUR FULL NAME" name="name" onChange={handleChange} />
        <label>ADDRESS</label>
        <textarea
          rows="2"
          cols="5"
          type="textarea"
          placeholder="CURRENT ADDRESS"
          name="address"
          onChange={handleChange}
        ></textarea>
        <label>PIN-CODE</label>
        <input type="tel" placeholder="ENTER PIN_CODE" name="PinCode" onChange={handleChange} />
        <label>CITY</label>
        <input type="text" placeholder="ENTER CITY" name="city" onChange={handleChange} />
        <label>STATE</label>
        <input type="text" placeholder="ENTER STATE" name="state" onChange={handleChange} />
        <label>COUNTRY</label>
        <input type="text" placeholder="ENTER COUNTRY" name="country" onChange={handleChange} />
        <label>CONTACT-NUMBER</label>
        <input
          type="tel"
          placeholder="ONLY 10 DIGITS ALLOWED"
          name="contactNumber"
          onChange={handleChange}
        />
        <span>
          <input type="checkbox" id="myCheck" name="test" />
          &nbsp; &nbsp;
          <a href="https://policies.google.com/terms?hl=en-IN&fg=1" className="relink">
            <u>Terms & Conditions </u>
          </a>
        </span>
        <button onClick={handleSubmit}>Register</button>
        {/* <p>This is an Error!</p> */}
        <span>
          Already have an Account?
          <Link to="/login" className="relink">
            &nbsp;<u>Login</u>
          </Link>
        </span>
      </form>
    </div>
  );
};

export default Register;
